import React from 'react';
import {useDispatch} from 'react-redux';
import {enterEditMode, leaveEditMode} from './action';

const months = ["January","February","March","April","May",
"June","July","August", "September","September","October","November",
"December"];

//props - grab data from the parent
export function Memory(props){
    const memory = props.memory;
    const dispatch = useDispatch();

    const onEdit = () => {
        dispatch(enterEditMode(memory));
    }

    const onCancel = () => {
        dispatch(leaveEditMode(memory));
    }

    if(memory.isEditing) {
        return(
            <div className ="memory">
            <div className="memory-left">
                <input type = "text"/>
                <input type = "text"/>
                <input type = "text"/>
                <button>save</button>
                <button onClick = {onCancel}>cancel</button>
            </div>
            <div className = "memory-right">
                <textarea/>
            </div>
        </div>
        );
    }else{

    return(
        // CSS implementation from App.css, inner function
        <div className ="memory">
            <div className="memory-left">
                <span className ="year">{memory.year}</span>
                <span>{months[memory.month - 1]}{memory.day}</span>
                <button onClick = {onEdit}>Edit Order</button>
            </div>
            <div className = "memory-right">
                {memory.message}
            </div>
        </div>
    );
    }
}
