// useEffect show the output once the user enters the website
import React, {useEffect} from 'react';
import './App.css';
import {Memory} from './Memory';
// Hook
import {useSelector, useDispatch} from 'react-redux';
import {loadDay, startAddingOrder} from './action';

//getting data from the system
const date = new Date(); //js build in class
const year = date.getFullYear();
const month = date.getMonth() + 1;
const day = date.getDate();



function App() {
  // get data(list of memoris) from store
  const memories = useSelector(state => state.memories);

  const dispatch = useDispatch();

 useEffect(()=>{
    dispatch(loadDay(month,day));
},[dispatch]);

  const onAdd = () =>{
    dispatch(startAddingOrder(year,month,day));
  }

  const APP = () => <PersonList />;

  const PersonList= () => {
   return <Person />;
  };

  const Person = () => {
    return <div className = "title">
      {/* <img src = "src/prof_Jay.jpg" alt = "Jay Professional"/> */}
        <h1>Food Ordering App</h1>
      </div>
  }

  const BottomDescription = () => <Copyright />;

  const Copyright = () => {
    return <bottomDescription />;
  };

  const bottomDescription = () => {
    return <div class = "description">
       <div>Copyright &copy;2020 Jay's WebWork</div>
       <div>Send an email to zhuj3128.uwec.edu for questions</div>
       <div>Pictures taken from (website name)</div>
    </div>
  }

  const TopDescription = () => <Intro />;

  const Intro = () => {
    return <topDescription />;
  };

  const topDescription = () => {
    return <div class = "description">
       <div>This application allow the user to enter their food desire reaching date and the food name that they want to order. Also a menu is provided when the mouse moved to the menu button</div>
    </div>
  }

//dispatch dependency, only run once
  return (
    <div id = "memories-root">
      {/* map function - Array of memories into array components */}
      {/* <button onClick={onLoad}>load</button> */}
      <APP></APP>
      <TopDescription></TopDescription>
      <div className = "menu">
            Menu
                <ul className = "dropdown">
                    <li>Spicy Fried Rice </li>
                    <li>Non - Spicy Fried Rice</li>
                    <li>Mac and Cheese</li>
                    <li>Ramen Bonjour</li>
                    <li>Fish and Chips</li>
                </ul>
        </div>
      <button onClick = {onAdd}>Order a Meal</button>
      {memories.map(memory => <Memory key = {memory.id} memory={memory}/>)}
      <BottomDescription></BottomDescription>
    </div>
  );
}

export default App;
