import React from 'react';
import './App.css';
import {Memory} from './Memory';
// Hook
import {useSelector, useDispatch} from 'react-redux';
import {loadMemories} from './Action';



function App() {
  // get data(list of memoris) from store
  const memories = useSelector(state => state.memories);

  const dispatch = useDispatch();

  dispatch(loadMemories([
    {id: 3, year: 2020, month: 4, day:7, message: "Spicy Style Fried Rice"},
    {id: 2, year: 2021, month: 4, day:7, message: "Fish and Chips"},
    {id: 1, year: 2022, month: 4, day:7, message: "Mac and Cheese"},
  ]));

  return (
    <div id = "memories-root">
      {/* map function - Array of memories into array components */}
      {memories.map(memory => <Memory memory={memory}/>)}
    </div>
  );
}

export default App;
